import { useState, useId } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function DisplayButton({ icon, text, size }) {
  const id = useId();
  const [isClicked, setIsClicked] = useState(false);
  console.log("" + icon + " : " + isClicked);

  return (
    <button
      id={id}
      className="flex text-darkTone border-darkTone border-2 hover:bg-darkTone hover:text-white font-medium rounded-xl text-md px-7 py-2.5 me-2 mb-2"
      onClick={() => {
        setIsClicked(!isClicked);
      }}
    >
      <FontAwesomeIcon size={size} icon={icon} />
      {isClicked ? <div>text</div> : null}
    </button>
  );
}

export default DisplayButton;
