import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function DisplayButton({ icon, text, size }) {
  const [isExpanded, setIsExpanded] = useState(false);
  // const spacer =

  return (
    <button
      className={`font-medium text-md h-20 flex items-center justify-center border-darkTone border-2 rounded-xl  transition-all duration-500 ease-in-out  ${
        isExpanded
          ? "w-80 bg-darkTone text-white"
          : "w-20 bg-none text-darkTone"
      }`}
      onClick={() => {
        setIsExpanded(!isExpanded);
      }}
    >
      <div
        className={` transition-all ${
          isExpanded
            ? "w-0 ease-in duration-[330ms] visible "
            : "w-0 ease-out duration-[330ms] invisible "
        }`}
      />
      <FontAwesomeIcon
        className={` transition-all ${
          isExpanded
            ? " ease-in duration-[700ms]   translate-x-5"
            : " ease-out duration-200}   translate-x-18"
        }`}
        size={size}
        icon={icon}
      />
      <div
        className={` font-racing text-xl transition-all text-nowrap ${
          isExpanded
            ? "opacity-100 ease-in duration-[700ms] visible  translate-x-5"
            : "opacity-0 ease-out duration-200 invisible  "
        }`}
      >
        {text}
      </div>
    </button>
  );
}

export default DisplayButton;
