import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function DisplayButton({ icon, text, size }) {
  const [isExpanded, setIsExpanded] = useState(false);

  return (
    <button
      className={`font-medium text-md h-20 flex justify-center items-center border-darkTone border-2 rounded-xl  transition-all duration-500 ease-in-out  ${
        isExpanded
          ? "w-96 bg-darkTone text-white"
          : "w-20 bg-none text-darkTone"
      }`}
      onClick={() => {
        setIsExpanded(!isExpanded);
      }}
    >
      <FontAwesomeIcon size={size} icon={icon} />
      <div
        className={`transition-opacity duration-3000 ease-in-out ${
          isExpanded ? "opacity-100" : "opacity-0"
        }`}
      >
        {text}
      </div>
    </button>
  );
}

export default DisplayButton;
