import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function DisplayButton({ icon, text, size, copy }) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [showAlert, setShowAlert] = useState(false);

  const copyText = () => {};

  const notifyUser = () => {
    setShowAlert(true);
    setTimeout(() => {
      setShowAlert(false);
    }, 2000);
  };

  return (
    <button
      className={`font-medium text-md h-20 p-[1.4rem] flex justify-start items-center justify border-darkTone border-2 rounded-xl  transition-all duration-500 ease-in-out  ${
        isExpanded
          ? "max-w-full bg-darkTone text-white"
          : "max-w-20 bg-none text-darkTone"
      }`}
      onClick={() => {
        setIsExpanded(!isExpanded);
      }}
    >
      <FontAwesomeIcon size={size} icon={icon} />
      <div
        className={` p-2 font-racing text-xl transition-opacity text-nowrap ${
          isExpanded
            ? "delay-300 opacity-100 ease-in duration-300 visible"
            : "opacity-0 ease-in duration-200 invisible"
        }`}
      >
        {text}
      </div>
      <FontAwesomeIcon
        className={` p-2 font-racing text-xl transition-opacity text-nowrap ${
          isExpanded
            ? "delay-300 opacity-100 ease-in duration-300 visible"
            : "opacity-0 ease-in duration-200 invisible"
        }`}
        onClick={() => {
          copyText();
          notifyUser();
        }}
        size="1x"
        icon={copy}
      />
      {showAlert || (
        <div
          id="alert-5"
          class="flex items-center p-4 rounded-lg bg-gray-50 dark:bg-gray-800"
          role="alert"
        >
          <svg
            class="flex-shrink-0 w-4 h-4 dark:text-gray-300"
            aria-hidden="true"
            xmlns="http://www.w3.org/2000/svg"
            fill="currentColor"
            viewBox="0 0 20 20"
          >
            <path d="M10 .5a9.5 9.5 0 1 0 9.5 9.5A9.51 9.51 0 0 0 10 .5ZM9.5 4a1.5 1.5 0 1 1 0 3 1.5 1.5 0 0 1 0-3ZM12 15H8a1 1 0 0 1 0-2h1v-3H8a1 1 0 0 1 0-2h2a1 1 0 0 1 1 1v4h1a1 1 0 0 1 0 2Z" />
          </svg>
          <span class="sr-only">Info</span>
          <div class="ms-3 text-sm font-medium text-gray-800 dark:text-gray-300">
            A simple dark alert with an{" "}
            <a href="#" class="font-semibold underline hover:no-underline">
              example link
            </a>
            . Give it a click if you like.
          </div>
          <button
            type="button"
            class="ms-auto -mx-1.5 -my-1.5 bg-gray-50 text-gray-500 rounded-lg focus:ring-2 focus:ring-gray-400 p-1.5 hover:bg-gray-200 inline-flex items-center justify-center h-8 w-8 dark:bg-gray-800 dark:text-gray-300 dark:hover:bg-gray-700 dark:hover:text-white"
            data-dismiss-target="#alert-5"
            aria-label="Close"
          >
            <span class="sr-only">Dismiss</span>
            <svg
              class="w-3 h-3"
              aria-hidden="true"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 14 14"
            >
              <path
                stroke="currentColor"
                stroke-linecap="round"
                stroke-linejoin="round"
                stroke-width="2"
                d="m1 1 6 6m0 0 6 6M7 7l6-6M7 7l-6 6"
              />
            </svg>
          </button>
        </div>
      )}
    </button>
  );
}

export default DisplayButton;
