import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function DisplayButton({ icon, text, size, state, setter }) {
  return (
    <button
      className="flex text-darkTone border-darkTone border-2 hover:bg-darkTone hover:text-white font-medium rounded-xl text-md px-7 py-2.5 me-2 mb-2"
      onClick={() => {
        setter(!state);
      }}
    >
      <FontAwesomeIcon size={size} icon={icon} />
      <div className={`${state ? `block` : `hidden`}`}>text</div>
    </button>
  );
}

export default DisplayButton;
